shrinkScroll();function shrinkScroll(){var header=document.querySelector('header');var logo=document.querySelector('header h1 a');var minMenu=document.querySelector('#menu-toggle');window.addEventListener('scroll',function(){if(window.pageYOffset>80){header.classList+=' small-head';logo.classList+=' small-logo';minMenu.style.top='22%'}else{header.classList='';logo.classList='';minMenu.style.top=''}})}
